mapper.insert:
insert into tbl_reply( bno
					 , replytext
					 , replyer )
			   values( #{bno}
			   		 , #{replytext}
			   		 , #{replyer})

mapper.update:
update tbl_reply
   set replytext = #{replytext}
     , updatedt = now()
 where rno = #{rno}

mapper.select:
select *
  from tbl_reply
 where bno = #{bno}
 order by rno desc
 limit #{cri.pageStart}, #{cri.perPageNum}

select count(bno)
  from tbl_reply
 where bno = #{bno}

select bno
  from tbl_reply
 where rno = #{rno}
